    func indexOf(str: String) -> (Int) {
        let cols: [String] = ["A", "B", "C", "D", "E", "F", "G", "H", "J", "K", "L", "M", "N", "P", "Q", "R"]
        
        var index: Int = 0
        
        for col in cols {
            if col == str {
                return index
            }
            index += 1
        }
        
        return 0
    }
    
    func changeDoubleSample() {
        let C8 = indexOf(str: matrixSLotMax[lotSelectedIndex][inspectionLevelSelectedIndex])
        
        if inspectionOptionSelectedText == "Normal" {
            doubleSampleSizeN1.text = matrixS2Normal1LotMax[C8][aqlSelectedIndex]
            doubleAcceptOn1.text = matrixS2Normal2LotMax[C8*2][aqlSelectedIndex*2]
            doubleRejectOn1.text = matrixS2Normal2LotMax[C8*2][aqlSelectedIndex*2+1]
            doubleAcceptOn2.text = matrixS2Normal2LotMax[C8*2+1][aqlSelectedIndex*2]
            doubleRejectOn2.text = matrixS2Normal2LotMax[C8*2+1][aqlSelectedIndex*2+1]
        }
        else if inspectionOptionSelectedText == "Reduced" {
            doubleSampleSizeN1.text = matrixS2Reduced1LotMax[C8][aqlSelectedIndex]
            doubleAcceptOn1.text = matrixS2Reduced2LotMax[C8*2][aqlSelectedIndex*2]
            doubleRejectOn1.text = matrixS2Reduced2LotMax[C8*2][aqlSelectedIndex*2+1]
            doubleAcceptOn2.text = matrixS2Reduced2LotMax[C8*2+1][aqlSelectedIndex*2]
            doubleRejectOn2.text = matrixS2Reduced2LotMax[C8*2+1][aqlSelectedIndex*2+1]
        }
        else if inspectionOptionSelectedText == "Tightened" {
            doubleSampleSizeN1.text = matrixS2Tightened1LotMax[C8][aqlSelectedIndex]
            doubleAcceptOn1.text = matrixS2Tightened2LotMax[C8*2][aqlSelectedIndex*2]
            doubleRejectOn1.text = matrixS2Tightened2LotMax[C8*2][aqlSelectedIndex*2+1]
            doubleAcceptOn2.text = matrixS2Tightened2LotMax[C8*2+1][aqlSelectedIndex*2]
            doubleRejectOn2.text = matrixS2Tightened2LotMax[C8*2+1][aqlSelectedIndex*2+1]
        }
        else {
            doubleSampleSizeN1.text = "NA"
        }
        
        if doubleAcceptOn1.text != "#N/A" {
            doubleSampleSizeCode.text = matrixSLotMax[lotSelectedIndex][inspectionLevelSelectedIndex]
        }
        else {
            doubleSampleSizeCode.text = "Use Single Plan"
        }
        
        doubleSampleSizeN2.text = doubleSampleSizeN1.text
    }
    
